        try {
            Integer.parseInt(s);
            return true;
        }
        catch (NumberFormatException nfe) {
            return false;
        }
    }

    private static void swap(final String[] arr, int i, int j) {
        final String tmp = arr[i];
        arr[i] = arr[j];
        arr[j] = tmp;
    }

